/** @ignore */
/**
 * Some very basic linear algebra functionalities for 3D vectors and 3x3
 * matrixes.
 */
export declare type Vector3D = [number, number, number];
export declare type Matrix = [Vector3D, Vector3D, Vector3D];
export declare function determinant(matrix: Matrix): number;
export declare function invert(matrix: Matrix): Matrix;
export declare function dot(matrix: Matrix, vector: Vector3D): Vector3D;
export declare function angle(lhs: Vector3D, rhs: Vector3D): number;
export declare function norm(vector: Vector3D): number;
